#include <iostream>#include <fstream>#include <vector>using namespace std;class Solution {public:    std::vector<int> twoSum(std::vector<int>& nums, int target) {        for (int i = 0; i < nums.size(); ++i) {            for (int j = i + 1; j < nums.size(); ++j) {                if (nums[i] + nums[j] == target) {                    return { i, j };                }            }        }        return{ -1, -1 };    }};int main() {    ifstream infile("in.txt"); // 打开输入文件    int target;    infile >> target; // 读取目标值    std::vector<int> nums;    int num;    while (infile >> num)    {        nums.push_back(num); // 读取数组元素    }    infile.close(); // 关闭文件    // 创建Solution对象    Solution solution;    // 调用twoSum函数    std::vector<int> result = solution.twoSum(nums, target);    if (result == std::vector<int>{-1, -1}) {        std::cout << "No two sum solution" << std::endl;    }    else {        std::cout << "Index1: " << result[0] << ", Index2: " << result[1] << std::endl;    }    return 0;}